import React from "react";


export default class GameNumber extends React.Component{
    constructor(props){
        super(props);
        console.log(this.props.location(this.props.col, this.props.row));
        console.log(this.props.col + ' ' + this.props.row);
    }

    render(){
        return(
            <div className="gameNumber" style={{transform: `translate(${this.props.location(this.props.col)}, ${this.props.row})`}}>
                <span className="number">{this.props.value}</span>
            </div>
        );
    }
}
