import React from "react";
export default class GameNumber extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        return(
            <div className={`gameNumber ${'gameNumber'+'ds'}`} style={{left: `${this.props.location(this.props.col, this.props.row)[0]}px`, top: `${this.props.location(this.props.row, this.props.row)[1]}px`}}>
                <span className="number">{this.props.value}</span>
            </div>
        );
    }
}
