import React from "react";
import GameRow from "./GameRow";
export default class GameBox extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            gameMap: this.props.gameMap,
        };
    }

    render(){
        return(
            <div className="gameBox">
                {this.state.gameMap.map(
                    (element, index) => {
                        return(
                            <GameRow key={index} id={index}/>
                        );
                    }
                )}
            </div>
        );
    }
}
